import React from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import swal from "sweetalert";

export default function ResetNewPassword() {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const { id, resetToken } = useParams();

  function handlePassword(e) {
    setPassword(e.target.value);
    console.log(password);
  }
  function handleConfirmPassword(e) {
    setConfirmPassword(e.target.value);
    console.log(confirmPassword);
  }

  async function handleSubmit() {
    const passwordMatch = password === confirmPassword;
    console.log(passwordMatch);
    const url = "http://localhost:3500/updatePassword";
    const requestOptions = {
      method: "POST",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "content-type": "application/json",
      },
      body: JSON.stringify({ password: password, id: id, token: resetToken }),
    };
    if (passwordMatch) {
      await fetch(url, requestOptions)
        .then((response) => {
          console.log(response.status);
          if (response.status === 201) {
            swal("Password updated successfully");
          } else if (response.status === 409) {
            swal(
              "Username or EmailId already exists! Try entering another username"
            );
          }
        })

        .catch((error) => console.log("Form submit error", error));
    } else {
      swal("Password doesnot match");
    }
  }

  return (
    <div className="register">
      <h3>Reset Password</h3>
      <form className="form">
        <input
          onChange={handlePassword}
          type="password"
          placeholder="Enter New Password"
          name="password"
        ></input>
        <input
          onChange={handleConfirmPassword}
          type="password"
          placeholder="Confirm Password"
          name="confirmPassword"
        ></input>
        <button onSubmit={handleSubmit} type="submit">
          Register
        </button>
      </form>
    </div>
  );
}
